targets.expr:
100 *
sum by (label_cloud_google_com_compute_class) (
  (
    label_replace(
      node_memory_MemAvailable_bytes
      * on (instance) group_left(nodename) node_uname_info,
      "node", "$1", "nodename", "(.*)"
    )
  )
  * on (node) group_left(label_cloud_google_com_compute_class)
    max by (node, label_cloud_google_com_compute_class) (
      kube_node_labels{label_cloud_google_com_compute_class!=""}
    )
)
/
sum by (label_cloud_google_com_compute_class) (
  kube_node_status_allocatable{resource="memory",unit="byte"}
  * on (node) group_left(label_cloud_google_com_compute_class)
    max by (node, label_cloud_google_com_compute_class) (
      kube_node_labels{label_cloud_google_com_compute_class!=""}
    )
)

100 *
sum by (label_cloud_google_com_compute_class) (
  (
    kube_pod_container_resource_requests{resource="cpu",unit="core",namespace!=""}
    * on (namespace,pod) group_left(node)
      kube_pod_info{node!=""}
  )
  * on (node) group_left(label_cloud_google_com_compute_class)
    max by (node, label_cloud_google_com_compute_class) (
      kube_node_labels{label_cloud_google_com_compute_class!=""}
    )
)
/
sum by (label_cloud_google_com_compute_class) (
  kube_node_status_allocatable{resource="cpu",unit="core"}
  * on (node) group_left(label_cloud_google_com_compute_class)
    max by (node, label_cloud_google_com_compute_class) (
      kube_node_labels{label_cloud_google_com_compute_class!=""}
    )
)

topk(
  10,
  100 *
  sum by (node) (
    kube_pod_container_resource_requests{resource="memory",unit="byte",namespace!=""}
    * on (namespace,pod) group_left(node)
      kube_pod_info{node!=""}
  )
  /
  sum by (node) (
    kube_node_status_allocatable{resource="memory",unit="byte"}
  )
)

topk(
  10,
  100 *
  sum by (node) (
    kube_pod_container_resource_requests{resource="cpu",unit="core",namespace!=""}
    * on (namespace,pod) group_left(node)
      kube_pod_info{node!=""}
  )
  /
  sum by (node) (
    kube_node_status_allocatable{resource="cpu",unit="core"}
  )
)

topk(
  30,
  sum by (namespace,pod) (
    increase(kube_pod_container_status_restarts_total{namespace!="",container!="POD"}[24h])
  )
)

sum by (namespace,pod) (
  increase(kube_pod_container_status_restarts_total{namespace!="",container!="POD"}[7d])
)
and on (namespace,pod)
topk(
  30,
  sum by (namespace,pod) (
    increase(kube_pod_container_status_restarts_total{namespace!="",container!="POD"}[24h])
  )
)

max by (namespace,pod) (
  max_over_time(
    kube_pod_container_status_last_terminated_reason{
      namespace!="",container!="POD",reason="OOMKilled"
    }[24h]
  )
)
and on (namespace,pod)
topk(
  30,
  sum by (namespace,pod) (
    increase(kube_pod_container_status_restarts_total{namespace!="",container!="POD"}[24h])
  )
)

max by (namespace,pod) (
  max_over_time(
    kube_pod_container_status_last_terminated_reason{
      namespace!="",container!="POD",reason="OOMKilled"
    }[7d]
  )
)
and on (namespace,pod)
topk(
  30,
  sum by (namespace,pod) (
    increase(kube_pod_container_status_restarts_total{namespace!="",container!="POD"}[24h])
  )
)

topk(
  15,
  sum by (namespace, pod, container) (
    rate(container_cpu_cfs_throttled_periods_total{container!="POD", namespace!="", pod!="", container!="", container!="POD"}[5m])
  )
  /
  clamp_min(
    sum by (namespace, pod, container) (
      rate(container_cpu_cfs_periods_total{container!="POD", namespace!="", pod!="", container!="", container!="POD"}[5m])
    ),
    1
  )
)

topk(
  10,
  clamp_min(
    (
      sum by (namespace) (
        kube_pod_container_resource_requests{resource="memory", namespace!=""}
        and on(namespace,pod)
        (kube_pod_status_phase{phase="Running"} == 1)
      )
      -
      sum by (namespace) (
        max_over_time(
          container_memory_working_set_bytes{namespace!="",pod!="",container!="",image!=""}[6h]
        )
      )
    ),
    0
  )
)

topk(
  10,
  clamp_min(
    (
      sum by (namespace) (
        kube_pod_container_resource_requests{resource="cpu", namespace!=""}
        and on(namespace,pod)
        (kube_pod_status_phase{phase="Running"} == 1)
      )
      -
      sum by (namespace) (
        max_over_time(
          rate(container_cpu_usage_seconds_total{namespace!="",pod!="",container!="",image!=""}[2m])
        [6h:]
        )
      )
    ) * 1000,
    0
  )
)

clamp(
  (
    (
      sum by (pod, container) (
        kube_pod_container_resource_requests{
          namespace="$namespace",
          resource="memory"
        }
        and on(pod)
        (kube_pod_status_phase{phase="Running"} == 1)
      )
      /
      clamp_min(
        sum by (pod, container) (
          container_memory_working_set_bytes{
            namespace="$namespace",
            pod!="",
            container!="",
            image!=""
          }
        ),
        100 * 1024 * 1024
      )
    ) - 1
  ) * 100,
  -100,
  300
)

max by (pod, container) (
  kube_pod_container_resource_requests{
    namespace="$namespace",
    resource="memory",
    unit="byte"
  }
) / 1024 / 1024

max by (pod, container) (
  kube_pod_container_resource_requests{
    namespace="$namespace",
    resource="memory",
    unit="byte"
  }
) / 1024 / 1024

ceil(
  (
    1.2 * max by (pod, container) (
      quantile_over_time(
        0.70,
        rate(container_cpu_usage_seconds_total{namespace="$namespace",pod!="",container!="",image!=""}[5m])[7d:5m]
      )
    )
  ) * 1000 / 10
) * 10

max by (pod, container) (
  kube_pod_container_resource_requests{namespace="$namespace", resource="cpu"}
) * 1000

ceil(
  (
    1.2 * max by (pod, container) (
      quantile_over_time(
        0.95,
        rate(container_cpu_usage_seconds_total{namespace="$namespace",pod!="",container!="",image!=""}[5m])[7d:5m]
      )
    )
  ) * 1000 / 10
) * 10

max by (pod, container) (
  kube_pod_container_resource_limits{namespace="$namespace",pod!="",container!="",resource="cpu",unit="core"}
) * 1000
